mapper.select:
SELECT tag_id
FROM post_tags
WHERE post_id = #{postId}

SELECT p.id, p.title, p.cover_url AS coverUrl, p.views, u.username AS authorName, COUNT(pt.tag_id) AS common_tags FROM posts p JOIN post_tags pt ON p.id = pt.post_id JOIN user u ON p.user_id = u.id WHERE pt.tag_id IN
#{tagId}
AND p.id != #{excludeId} AND p.status = 'published' GROUP BY p.id ORDER BY common_tags DESC LIMIT #{limit}

mapper.update:
UPDATE posts
SET views = views + 1 
WHERE id = #{postId}
